using EventLink.API.Util;
using EventLink.DataAccess;
using EventLink.DataAccess.Models;
using MongoDB.Bson;
using System;
using System.Collections.Generic;
using System.Linq;

namespace EventLink.API.Services
{
    public interface IUserService
    {
        /* User */
        User GetUser(string userId);

        User GetUserByEmail(string email);
        void CreateUser(User obj);
        void UpdateUser(User obj);
        void DeactivateUser(string userId);
        IEnumerable<User> SearchUsers(string query);
        void UploadProfilePicture(string userId, string base64ImageData);

        /* Events */
        IEnumerable<Event> GetFavoriteEvents(string userId);
        void AddFavoriteEvent(string userId, string eventId);
        void RemoveFavoriteEvent(string userId, string eventId);
        IEnumerable<Event> GetParticipatingEvents(string userId);
        void AddParticipatingEvent(string userId, string eventId);
        void RemoveParticipatingEvent(string userId, string eventId);
        IEnumerable<Event> GetPastEvents(string userId);
        void AddPastEvent(string userId, string eventId);
        void RemovePastEvent(string userId, string eventId);

        /* Buddy */
        IEnumerable<User> GetBuddies(string userId);
        List<User> GetParticipatingBuddies(string userId, string eventId);
        void AddBuddy(string userId, string buddyId);
        void RemoveBuddy(string userId, string buddyId);

        /* Payment */
        IEnumerable<Payment> GetOrderHistory(string userId);
    }

    public class UserService : IUserService
    {
        private static readonly Lazy<IUserService> instance =
            new Lazy<IUserService>(() => new UserService());

        public static IUserService Instance => instance.Value;

        private readonly DataAccess.Services.UserService _userService = DataAccess.Services.UserService.Instance;
        private readonly DataAccess.Services.EventService _eventService = DataAccess.Services.EventService.Instance;
        private readonly DataAccess.Services.PaymentService _paymentService = DataAccess.Services.PaymentService.Instance;
        private readonly IFTPClient _ftpClient = FTPClient.Instance;

        private UserService()
        {

        }

        public User GetUser(string userId)
        {
            try
            {
                return _userService.GetUser(userId);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public User GetUserByEmail(string email)
        {
            try
            {
                return _userService.GetUserByEmail(email);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void CreateUser(User obj)
        {
            try
            {
                /* Generate user id */
                var id = ObjectId.GenerateNewId().ToString();
                obj.Id = id;

                /* Create user */
                _userService.CreateUser(obj);

                /* Upload default profile picture for user if user has no picture*/
                if (string.IsNullOrEmpty(obj.PicUrl))
                {
                    const string base64DefaultImage = "";
                    UploadProfilePicture(obj.Id, base64DefaultImage);
                }
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void UpdateUser(User obj)
        {
            try
            {
                _userService.UpdateUser(obj);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void DeactivateUser(string userId)
        {
            try
            {
                _userService.DeleteUser(userId);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<User> SearchUsers(string query)
        {
            try
            {
                return _userService.SearchUsers(query);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void UploadProfilePicture(string userId, string base64ImageData)
        {
            if (string.IsNullOrEmpty(userId))
            {
                throw new Exception("userId is null or empty!");
            }

            if (string.IsNullOrEmpty(base64ImageData))
            {
                throw new Exception("base64ImageData is null or empty!");
            }

            // check if user actually exists
            User user;

            try
            {
                user = _userService.GetUser(userId);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }

            // if user exists, upload the image data.
            try
            {
                var picFileName = userId + "_profile_pic.jpg";
                var picUrl = "http://bak.eventlink.ml:8000/" + picFileName;

                _ftpClient.UploadFile(picFileName, base64ImageData);

                user.PicUrl = picUrl;
                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<Event> GetFavoriteEvents(string userId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var favoriteEventIds = user.FavoriteEvents;
                return favoriteEventIds.Select(eventId => _eventService.GetEvent(eventId)).ToList();
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void AddFavoriteEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);

                /* Does the event actually exist? */
                _eventService.GetEvent(eventId);

                /* Is the event already a favorite of the user? */
                var match = user.FavoriteEvents.FirstOrDefault(fEventId => fEventId.Equals(eventId));

                if (match != null)
                {
                    throw new Exception("Event (" + eventId + ") is already a favorite of User (" + userId + ")!");
                }

                var favoriteEventList = user.FavoriteEvents.ToList();
                favoriteEventList.Add(eventId);
                user.FavoriteEvents = favoriteEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void RemoveFavoriteEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var favoriteEventList = user.FavoriteEvents.ToList();

                favoriteEventList.Remove(eventId);
                user.FavoriteEvents = favoriteEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<Event> GetParticipatingEvents(string userId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var participatingEventIds = user.ParticipatingEvents;
                return participatingEventIds.Select(eventId => _eventService.GetEvent(eventId)).ToList();
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void AddParticipatingEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);

                /* Does the event actually exist? */
                _eventService.GetEvent(eventId);

                /* Is the user already participating in the event? */
                var match = user.ParticipatingEvents.FirstOrDefault(pEventId => pEventId.Equals(eventId));

                if (match != null)
                {
                    throw new Exception("User (" + userId + ") is already participating in Event (" + eventId + ")!");
                }

                var participatingEventList = user.ParticipatingEvents.ToList();
                participatingEventList.Add(eventId);
                user.ParticipatingEvents = participatingEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void RemoveParticipatingEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var participatingEventList = user.ParticipatingEvents.ToList();

                participatingEventList.Remove(eventId);
                user.ParticipatingEvents = participatingEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<Event> GetPastEvents(string userId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var pastEventIds = user.PastEvents;
                return pastEventIds.Select(pastEventId => _eventService.GetEvent(pastEventId)).ToList();
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void AddPastEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);

                /* Does the event actually exist? */
                var eventObj = _eventService.GetEvent(eventId);

                /* Is the event actually over? */
                if (DateTime.Parse(eventObj.Dates.LocalStartDate) > DateTime.Now)
                {
                    throw new Exception("Event (" + eventId + ") with localStartDate (" + eventObj.Dates.LocalStartDate + ") is not in the past!");
                }

                /* Is the event already a past event of the user? */
                var match = user.PastEvents.FirstOrDefault(pEventId => pEventId.Equals(eventId));

                if (match != null)
                {
                    throw new Exception("Event (" + eventId + ") is already a past event of User (" + userId + ")!");
                }

                var pastEventList = user.PastEvents.ToList();
                pastEventList.Add(eventId);
                user.PastEvents = pastEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void RemovePastEvent(string userId, string eventId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var pastEventList = user.PastEvents.ToList();

                pastEventList.Remove(eventId);
                user.PastEvents = pastEventList;

                _userService.UpdateUser(user);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<User> GetBuddies(string userId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var buddyIds = user.Buddies;
                var buddyList = new List<User>();

                foreach (var buddyId in buddyIds)
                {
                    try
                    {
                        var buddy = _userService.GetUser(buddyId);
                        buddyList.Add(buddy);
                    }
                    catch (DAException)
                    {

                    }
                }
                return buddyList;
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public List<User> GetParticipatingBuddies(string userId, string eventId)
        {
            var participatingBuddies = new List<User>();

            try
            {
                /* Do the user and event actually exist? */
                var userObj = _userService.GetUser(userId);
                var eventObj = _eventService.GetEvent(eventId);

                /* Get all buddies of userId */
                var userBuddies = GetBuddies(userId);

                /*
                 * Check which userId in userBuddies that are
                 * participating in the specific eventId.
                 */
                participatingBuddies.AddRange(userBuddies.Where(buddy => buddy.ParticipatingEvents.Contains(eventId)));
            }
            catch (DAException e)
            {
                throw new Exception(e.Message, e);
            }

            return participatingBuddies;
        }

        public void AddBuddy(string userId, string buddyId)
        {
            try
            {
                /* Do the users actually exist? */
                var user = _userService.GetUser(userId);
                var buddy = _userService.GetUser(buddyId);

                /* Are you trying to add yourself as a buddy? (Forever alone) */
                if (userId.Equals(buddyId))
                {
                    throw new Exception("You can't add yourself as a buddy. Nice try. :)");
                }

                /* Is the buddy already a buddy of user? */
                var matchUser = user.Buddies.FirstOrDefault(bId => bId.Equals(buddyId));

                if (matchUser != null)
                {
                    throw new Exception("User (" + buddyId + ") is already a buddy of User (" + userId + ")!");
                }

                /* Is the user already a buddy of buddy? */
                var matchBuddy = buddy.Buddies.FirstOrDefault(bId => bId.Equals(userId));

                if (matchBuddy != null)
                {
                    throw new Exception("User (" + userId + ") is already a buddy of User (" + buddyId + ")!");
                }

                var userBuddyList = user.Buddies.ToList();
                var buddyBuddyList = buddy.Buddies.ToList();

                userBuddyList.Add(buddyId);
                buddyBuddyList.Add(userId);

                user.Buddies = userBuddyList;
                buddy.Buddies = buddyBuddyList;

                _userService.UpdateUser(user);
                _userService.UpdateUser(buddy);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public void RemoveBuddy(string userId, string buddyId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var userBuddyList = user.Buddies.ToList();

                if (!userBuddyList.Contains(buddyId))
                {
                    throw new Exception("User (Buddy) (" + buddyId + ") is not a buddy of User (User) (" + userId + ")!");
                }

                var buddy = _userService.GetUser(buddyId);
                var buddyBuddyList = buddy.Buddies.ToList();

                if (!buddyBuddyList.Contains(userId))
                {
                    throw new Exception("User (User) (" + userId + ") is not a buddy of User (Buddy) (" + buddyId + ")!");
                }

                userBuddyList.Remove(buddyId);
                user.Buddies = userBuddyList;

                buddyBuddyList.Remove(userId);
                buddy.Buddies = buddyBuddyList;

                _userService.UpdateUser(user);
                _userService.UpdateUser(buddy);
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

        public IEnumerable<Payment> GetOrderHistory(string userId)
        {
            try
            {
                var user = _userService.GetUser(userId);
                var paymentIds = user.Payments;
                return paymentIds.Select(paymentId => _paymentService.GetPayment(paymentId)).ToList();
            }
            catch (Exception e)
            {
                throw new Exception(e.Message, e);
            }
        }

    }
}